// ==========================================
// File: app/about/page.tsx
// ==========================================
import Image from "next/image";
import DentistGallery from "./DentistGallery";

interface Doctor {
  name: string;
  photo: string;
  joinDate: string;
  daysDone: string;
}

interface AboutData {
  about: {
    description?: string;
  };
  doctors: Doctor[];
  contact?: {
    address?: string;
    phone?: string;
    email?: string;
  };
}

export async function getAboutData(): Promise<AboutData> {
  try {
    const fs = require("fs/promises");
    const path = require("path");
    const dataFile = path.join(process.cwd(), "data", "clinic-info.json");
    const data = await fs.readFile(dataFile, "utf-8");
    const parsed = JSON.parse(data);

    return {
      about: parsed.about || {},
      doctors: parsed.doctors || [],
      contact: parsed.contact || {},
    };
  } catch (error) {
    console.error("Error loading data:", error);
    return {
      about: {},
      doctors: [],
      contact: {},
    };
  }
}

export default async function AboutPage() {
  const { about, doctors, contact } = await getAboutData();

  return (
    <section className="prose max-w-none">
      <h1>About Us</h1>
      {about.description && <p>{about.description}</p>}

      {/* Visi & Misi Section */}
      <div className="not-prose my-16 mx-[-1.24rem] lg:mx-[-6.8rem]">
        <div className="grid md:grid-cols-2 gap-0 overflow-hidden ">
          {/* Visi Section */}
          <div className=" p-12 transition-all duration-300 hover:bg-[#8E1616] hover:shadow-2xl group">
            <h2 className="text-3xl md:text-5xl font-medium text-gray-800 group-hover:text-white mb-8 group-hover:scale-105 transition-all duration-300">
              VISI
            </h2>
            <p className="text-gray-700 group-hover:text-white leading-relaxed text-base md:text-lg transition-all duration-300">
              Menjadi klinik gigi terkemuka yang unggul dalam pelayanan inovatif
              dan kepuasan pasien, dengan komitmen tinggi terhadap etika
              profesi, keselamatan pasien, serta kualitas perawatan yang
              konsisten. Klinik gigi kami bertekad menjadi rujukan utama bagi
              masyarakat di seluruh Kabupaten Pesisir Selatan, melalui
              peningkatan kapasitas tenaga medis, pemanfaatan teknologi terkini,
              serta budaya pelayanan yang empatik dan responsif terhadap
              kebutuhan setiap pasien.
            </p>
          </div>

          {/* Misi Section */}
          <div className="p-12 transition-all duration-300 hover:bg-[#8E1616] hover:shadow-2xl group">
            <h2 className="text-3xl md:text-5xl font-medium text-gray-800 group-hover:text-white mb-8 group-hover:scale-105 transition-all duration-300">
              MISI
            </h2>
            <ol className="space-y-1 text-gray-700 group-hover:text-white leading-relaxed text-sm md:text-base transition-all duration-300">
              <li className="flex gap-3">
                <span className="font-semibold flex-shrink-0">1.</span>
                <span>
                  Selalu berupaya memberikan pelayanan yang melampaui ekspektasi
                  pasien melalui standar mutu yang konsisten, inovasi, dan
                  responsif terhadap umpan balik.
                </span>
              </li>
              <li className="flex gap-3">
                <span className="font-semibold flex-shrink-0">2.</span>
                <span>
                  Menyediakan pelayanan terbaik dengan harga terjangkau, tanpa
                  mengorbankan keselamatan, kualitas, dan kenyamanan perawatan.
                </span>
              </li>
              <li className="flex gap-3">
                <span className="font-semibold flex-shrink-0">3.</span>
                <span>
                  Menjadi klinik gigi yang nyaman dan bersahabat bagi keluarga,
                  dengan pendekatan ramah anak, kenyamanan fasilitas, serta
                  suasana klinik yang menenangkan.
                </span>
              </li>
              <li className="flex gap-3">
                <span className="font-semibold flex-shrink-0">4.</span>
                <span>
                  Mengedepankan kerja sama tim yang solid dan kolaboratif,
                  berorientasi pada kepuasan pasien melalui koordinasi lintas
                  bidang dan komunikasi yang efektif.
                </span>
              </li>
              <li className="flex gap-3">
                <span className="font-semibold flex-shrink-0">5.</span>
                <span>
                  Memberikan edukasi kesehatan gigi dan mulut yang relevan,
                  mudah dipahami, serta berdampak pada peningkatan literasi
                  kesehatan masyarakat.
                </span>
              </li>
            </ol>
          </div>
        </div>
      </div>

      {/* Dentists Section */}
      {doctors.length > 0 && <DentistGallery doctors={doctors} />}

      {/* Address Section */}
      <div className="not-prose my-16">
        <div className="grid md:grid-cols-2 gap-8 items-start">
          {/* Left Column - Question */}
          <div className=" bg-[#8E1616] p-9">
            <h2 className="text-4xl md:text-5xl font-medium text-gray-100 mb-2">
              Dimana Klinik Kami?
            </h2>

            <div>
              <h3 className="text-lg font-medium text-gray-200 ">Alamat</h3>
              <p className="text-gray-300 leading-relaxed">
                {contact?.address ||
                  "Jl. Lintas Sumatera, Painan, Kabupaten Pesisir Selatan, Sumatera Barat"}
              </p>
            </div>

            <div>
              <h3 className="text-lg font-medium text-gray-200 ">Telepon</h3>
              <p className="text-gray-300">
                {contact?.phone || "+62 812-3456-7890"}
              </p>
            </div>

            <div>
              <h3 className="text-lg font-medium text-gray-200 ">Email</h3>
              <p className="text-gray-300">
                {contact?.email || "info@noerdental.com"}
              </p>
            </div>
          </div>

          {/* Right Column - google maps */}
          <div className="space-y-6">
            <div className="mt-9 w-full h-[400px]  overflow-hidden ">
              <iframe
                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3988.7852148076745!2d100.50992990915701!3d-1.3038954986782265!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x2fd3516761b25759%3A0xde7a3d7b4882ce3c!2sPraktek%20drg.%20Hidayati%20M.K.M%20dan%20dr%20.Widodo!5e0!3m2!1sid!2sid!4v1761470595622!5m2!1sid!2sid"
                width="100%"
                height="100%"
                style={{ border: 0 }}
                allowFullScreen
                loading="lazy"
                referrerPolicy="no-referrer-when-downgrade"
                title="Lokasi Klinik NoerDental"
              ></iframe>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";

interface AboutData {
  title: string;

  description: string;
  values: string[];
}

export default function AboutAdminPage() {
  const [data, setData] = useState<AboutData | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch("/api/clinic-info");
      if (!res.ok) throw new Error("Failed to fetch");
      const result = await res.json();
      setData(result.about);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!data) return;
    setSaving(true);
    try {
      // Fetch current data to merge
      const res = await fetch("/api/clinic-info");
      const currentData = await res.json();
      const updatedData = { ...currentData, about: data };

      const saveRes = await fetch("/api/clinic-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });
      if (!saveRes.ok) throw new Error("Failed to save");
      alert("Saved successfully!");
    } catch (err: any) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const updateData = (field: string, value: any) => {
    setData((prev) => (prev ? { ...prev, [field]: value } : prev));
  };

  if (loading) return <div>Loading...</div>;
  if (!data) return <div>Error loading data</div>;

  return (
    <div className="max-w-4xl mx-auto space-y-8 px-4 sm:px-6">
      <div className="flex justify-between items-center">
        <div>
          <Link
            href="/admin/clinic"
            className="font-black hover:underline mb-1 inline-block text-m"
          >
            Back‚Üê
          </Link>
          <h1 className="text-2xl font-bold">About Us Settings</h1>
        </div>
        <button
          onClick={handleSave}
          disabled={saving}
          className="w-full md:w-auto px-6 py-2 border-2 border-black font-medium hover:bg-black hover:text-white transition-colors"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </div>

      {error && (
        <div className="text-red-600 bg-red-50 p-3 rounded">{error}</div>
      )}

      {/* About Section */}
      <div className="bg-white border rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">About Configuration</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-1">Title</label>
            <input
              type="text"
              value={data.title}
              onChange={(e) => updateData("title", e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>

          <div>
            <label className="block text-sm font-medium mb-1">
              Description
            </label>
            <textarea
              value={data.description}
              onChange={(e) => updateData("description", e.target.value)}
              rows={3}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              Values (comma separated)
            </label>
            <input
              type="text"
              value={data.values.join(", ")}
              onChange={(e) =>
                updateData(
                  "values",
                  e.target.value.split(", ").map((v) => v.trim())
                )
              }
              className="w-full p-2 border rounded"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";

type Dentist = {
  id: string;
  name: string;
  email: string;
  isActive: boolean;
  hasPassword: boolean;
  createdAt: string;
  updatedAt: string;
};

export default function DentistsManagementPage() {
  const router = useRouter();
  const [dentists, setDentists] = useState<Dentist[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [formData, setFormData] = useState({ name: "", email: "" });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchDentists();
  }, []);

  const fetchDentists = async () => {
    try {
      const res = await fetch("/api/dentists");
      if (!res.ok) {
        if (res.status === 403) {
          router.push("/admin");
          return;
        }
        throw new Error("Failed to fetch");
      }
      const data = await res.json();
      setDentists(data);
    } catch (error) {
      console.error("Error fetching dentists:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddDentist = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    setError("");

    try {
      const res = await fetch("/api/dentists", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.error || "Failed to add dentist");
        return;
      }

      setShowAddModal(false);
      setFormData({ name: "", email: "" });
      fetchDentists();
      alert("Dentist added successfully! OTP sent to email.");
    } catch (error) {
      setError("Failed to add dentist");
    } finally {
      setSubmitting(false);
    }
  };

  const handleToggleActive = async (id: string, currentStatus: boolean) => {
    if (
      !confirm(
        `Are you sure you want to ${
          currentStatus ? "deactivate" : "activate"
        } this dentist?`
      )
    ) {
      return;
    }

    try {
      const res = await fetch(`/api/dentists/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isActive: !currentStatus }),
      });

      if (res.ok) {
        fetchDentists();
      }
    } catch (error) {
      alert("Failed to update dentist status");
    }
  };

  const handleDelete = async (id: string, name: string) => {
    if (
      !confirm(
        `Are you sure you want to delete ${name}? This action cannot be undone.`
      )
    ) {
      return;
    }

    try {
      const res = await fetch(`/api/dentists/${id}`, {
        method: "DELETE",
      });

      if (res.ok) {
        fetchDentists();
        alert("Dentist deleted successfully");
      } else {
        alert("Failed to delete dentist");
      }
    } catch (error) {
      alert("Failed to delete dentist");
    }
  };

  const handleResendOTP = async (id: string, email: string) => {
    if (!confirm(`Resend OTP to ${email}?`)) {
      return;
    }

    try {
      const res = await fetch(`/api/dentists/${id}/resend-otp`, {
        method: "POST",
      });

      const data = await res.json();

      if (res.ok) {
        alert("OTP resent successfully!");
      } else {
        alert(data.error || "Failed to resend OTP");
      }
    } catch (error) {
      alert("Failed to resend OTP");
    }
  };

  if (loading) {
    return (
      <div className="max-w-6xl mx-auto px-4 sm:px-6 py-8">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 sm:px-6 py-8 bg-white">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-4xl font-bold text-black">Dentists Management</h1>
          <p className="text-gray-600 mt-2">
            Manage dentist accounts and access
          </p>
        </div>
        <button
          onClick={() => setShowAddModal(true)}
          className="border-2 border-black hover:text-white px-6 py-3 font-semibold hover:bg-black transition-colors"
        >
          + Add Dentist
        </button>
      </div>

      {/* Table */}
      <div className="border border-black overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-100 border-b border-black">
            <tr>
              <th className="px-6 py-4 text-left text-sm font-bold text-black">
                Name
              </th>
              <th className="px-6 py-4 text-left text-sm font-bold text-black">
                Email
              </th>
              <th className="px-6 py-4 text-center text-sm font-bold text-black">
                Status
              </th>
              <th className="px-6 py-4 text-center text-sm font-bold text-black">
                Password
              </th>
              <th className="px-6 py-4 text-center text-sm font-bold text-black">
                Actions
              </th>
            </tr>
          </thead>
          <tbody>
            {dentists.length === 0 ? (
              <tr>
                <td colSpan={5} className="px-6 py-8 text-center text-gray-500">
                  No dentists registered yet
                </td>
              </tr>
            ) : (
              dentists.map((dentist) => (
                <tr
                  key={dentist.id}
                  className="border-b border-gray-200 hover:bg-gray-50"
                >
                  <td className="px-6 py-4 text-black font-medium">
                    {dentist.name}
                  </td>
                  <td className="px-6 py-4 text-gray-700">{dentist.email}</td>
                  <td className="px-6 py-4 text-center">
                    <span
                      className={`inline-block px-3 py-1 text-xs font-semibold ${
                        dentist.isActive
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      {dentist.isActive ? "Active" : "Inactive"}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-center">
                    <span
                      className={`inline-block px-3 py-1 text-xs font-semibold ${
                        dentist.hasPassword
                          ? "bg-blue-100 text-blue-800"
                          : "bg-yellow-100 text-yellow-800"
                      }`}
                    >
                      {dentist.hasPassword ? "Set" : "Pending"}
                    </span>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex gap-2 justify-center">
                      <button
                        onClick={() =>
                          handleToggleActive(dentist.id, dentist.isActive)
                        }
                        className="text-sm text-blue-600 hover:text-blue-800 font-medium"
                      >
                        {dentist.isActive ? "Deactivate" : "Activate"}
                      </button>
                      {!dentist.hasPassword && (
                        <button
                          onClick={() =>
                            handleResendOTP(dentist.id, dentist.email)
                          }
                          className="text-sm text-green-600 hover:text-green-800 font-medium"
                        >
                          Resend OTP
                        </button>
                      )}
                      <button
                        onClick={() => handleDelete(dentist.id, dentist.name)}
                        className="text-sm text-red-600 hover:text-red-800 font-medium"
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Add Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 max-w-md w-full mx-4 border-2 border-black">
            <h2 className="text-2xl font-bold text-black mb-6">
              Add New Dentist
            </h2>

            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 mb-4">
                {error}
              </div>
            )}

            <form onSubmit={handleAddDentist}>
              <div className="mb-4">
                <label className="block text-black font-semibold mb-2">
                  Name
                </label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) =>
                    setFormData({ ...formData, name: e.target.value })
                  }
                  className="w-full border border-black px-4 py-2 focus:outline-none focus:ring-2 focus:ring-black"
                  required
                />
              </div>

              <div className="mb-6">
                <label className="block text-black font-semibold mb-2">
                  Email
                </label>
                <input
                  type="email"
                  value={formData.email}
                  onChange={(e) =>
                    setFormData({ ...formData, email: e.target.value })
                  }
                  className="w-full border border-black px-4 py-2 focus:outline-none focus:ring-2 focus:ring-black"
                  required
                />
              </div>

              <div className="flex gap-4">
                <button
                  type="button"
                  onClick={() => {
                    setShowAddModal(false);
                    setError("");
                    setFormData({ name: "", email: "" });
                  }}
                  className="flex-1 border border-black px-6 py-3 font-semibold hover:bg-gray-100 transition-colors"
                  disabled={submitting}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="flex-1 bg-black text-white px-6 py-3 font-semibold hover:bg-gray-800 transition-colors disabled:opacity-50"
                  disabled={submitting}
                >
                  {submitting ? "Adding..." : "Add Dentist"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";

interface Doctor {
  id?: number;
  name: string;
  email?: string;
  photo: string;
  joinDate: string;
  daysDone: string;
  hasPassword?: boolean;
  user?: {
    id: string;
    email: string;
    isActive: boolean;
  };
}

export default function DoctorsAdminPage() {
  const [data, setData] = useState<Doctor[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await fetch("/api/doctor-profiles");
      if (!res.ok) throw new Error("Failed to fetch");
      const result = await res.json();
      setData(result || []);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async (doctor: Doctor, index: number) => {
    setSaving(true);
    setError(null);
    setSuccess(null);
    try {
      const res = await fetch("/api/doctor-profiles", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(doctor),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.error || "Failed to save");
      }

      const updatedDoctor = await res.json();

      // Update local state
      setData((prev) =>
        prev.map((d, i) =>
          i === index ? { ...updatedDoctor, hasPassword: d.hasPassword } : d
        )
      );

      if (doctor.email && !doctor.hasPassword) {
        setSuccess(
          `Doctor saved! OTP has been sent to ${doctor.email}. They can now set their password.`
        );
      } else {
        setSuccess("Doctor saved successfully!");
      }

      setEditingIndex(null);

      // Refresh data to get latest info
      setTimeout(() => fetchData(), 1000);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (doctor: Doctor, index: number) => {
    if (!confirm(`Delete ${doctor.name}?`)) return;

    if (!doctor.id) {
      // Just remove from local state if not saved yet
      setData((prev) => prev.filter((_, i) => i !== index));
      setEditingIndex(null);
      return;
    }

    try {
      const res = await fetch(`/api/doctor-profiles?id=${doctor.id}`, {
        method: "DELETE",
      });

      if (!res.ok) throw new Error("Failed to delete");

      setData((prev) => prev.filter((_, i) => i !== index));
      setSuccess("Doctor deleted successfully!");
      setEditingIndex(null);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const updateArrayItem = (index: number, field: string, value: any) => {
    setData((prev) =>
      prev.map((item, i) => (i === index ? { ...item, [field]: value } : item))
    );
  };

  const addToArray = () => {
    const newIndex = data.length;
    setData((prev) => [
      ...prev,
      {
        name: "",
        email: "",
        photo: "",
        joinDate: "",
        daysDone: "",
      },
    ]);
    setEditingIndex(newIndex);
  };

  const handleFileUpload = async (file: File, index: number) => {
    setUploading(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("/api/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Upload failed");
      }

      const result = await response.json();
      updateArrayItem(index, "photo", result.imageUrl);
    } catch (err: any) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  const handleImageChange = async (
    e: React.ChangeEvent<HTMLInputElement>,
    index: number
  ) => {
    const file = e.target.files?.[0];
    if (file) {
      await handleFileUpload(file, index);
    }
  };

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <div className="max-w-6xl mx-auto space-y-3 px-3 sm:px-4 py-4">
      <div className="flex justify-between items-center mb-4">
        <div>
          <Link
            href="/admin/clinic"
            className="font-black hover:underline mb-1 inline-block text-m"
          >
            Back‚Üê
          </Link>
          <h1 className="text-3xl font-bold">Dentist Management</h1>
          <p className="text-sm text-gray-600 mt-1">
            Add email to create dentist login accounts
          </p>
        </div>
      </div>

      {error && (
        <div className="text-red-600 bg-red-50 p-3 border border-red-600 mb-2 text-sm">
          {error}
        </div>
      )}

      {success && (
        <div className="text-green-600 bg-green-50 p-3 border border-green-600 mb-2 text-sm">
          {success}
        </div>
      )}

      {/* Doctors List */}
      <div className="space-y-0">
        {data.map((doctor, index) => (
          <div key={index}>
            {editingIndex === index ? (
              <div className="bg-gray-50 border-t border-gray-200 py-4 px-4">
                <div className="space-y-3">
                  <div className="flex items-center gap-4">
                    <div className="w-24 h-24 bg-gray-200 flex-shrink-0 overflow-hidden">
                      {doctor.photo && (
                        <img
                          src={doctor.photo}
                          alt={doctor.name}
                          className="w-full h-full object-cover"
                        />
                      )}
                    </div>
                    <div className="flex-1 space-y-2">
                      <div>
                        <label className="block text-xs font-medium mb-1">
                          Name *
                        </label>
                        <input
                          type="text"
                          value={doctor.name}
                          onChange={(e) =>
                            updateArrayItem(index, "name", e.target.value)
                          }
                          className="w-full p-2 border border-gray-300 text-sm"
                          placeholder="Dentist name"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-xs font-medium mb-1">
                          Email (for login access)
                          {doctor.hasPassword && (
                            <span className="ml-2 text-green-600">
                              ‚úì Account activated
                            </span>
                          )}
                        </label>
                        <input
                          type="email"
                          value={doctor.email || ""}
                          onChange={(e) =>
                            updateArrayItem(index, "email", e.target.value)
                          }
                          className="w-full p-2 border border-gray-300 text-sm"
                          placeholder="dentist@example.com"
                        />
                        <p className="text-xs text-gray-500 mt-1">
                          {!doctor.hasPassword
                            ? "OTP will be sent to this email for password setup"
                            : "Login account is active"}
                        </p>
                      </div>
                    </div>
                  </div>
                  <div>
                    <label className="block text-xs font-medium mb-1">
                      Photo Upload
                    </label>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => handleImageChange(e, index)}
                      className="w-full p-2 border border-gray-300 text-sm mb-2"
                      disabled={uploading}
                    />
                    {uploading && (
                      <p className="text-xs text-blue-600 mb-2">Uploading...</p>
                    )}
                  </div>
                  <div className="grid grid-cols-2 gap-3">
                    <div>
                      <label className="block text-xs font-medium mb-1">
                        Join Date
                      </label>
                      <input
                        type="month"
                        value={doctor.joinDate}
                        onChange={(e) =>
                          updateArrayItem(index, "joinDate", e.target.value)
                        }
                        className="w-full p-2 border border-gray-300 text-sm"
                      />
                    </div>
                    <div>
                      <label className="block text-xs font-medium mb-1">
                        Days Done
                      </label>
                      <input
                        type="text"
                        value={doctor.daysDone}
                        onChange={(e) =>
                          updateArrayItem(index, "daysDone", e.target.value)
                        }
                        className="w-full p-2 border border-gray-300 text-sm"
                        placeholder="123"
                      />
                    </div>
                  </div>
                  <div className="flex gap-2 pt-1">
                    <button
                      onClick={() => handleSave(doctor, index)}
                      disabled={saving || !doctor.name}
                      className="px-3 py-1 bg-green-600 text-white text-sm hover:bg-green-700 disabled:opacity-50"
                    >
                      {saving ? "Saving..." : "Save"}
                    </button>
                    <button
                      onClick={() => setEditingIndex(null)}
                      className="px-3 py-1 bg-gray-500 text-white text-sm hover:bg-gray-600"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={() => handleDelete(doctor, index)}
                      className="px-3 py-1 bg-red-500 text-white text-sm hover:bg-red-600"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <div
                className="border-t border-gray-200 py-4 px-4 hover:bg-gray-50 cursor-pointer transition-colors"
                onClick={() => setEditingIndex(index)}
              >
                <div className="flex items-start gap-4">
                  <div className="text-gray-400 font-medium text-sm pt-1 w-8">
                    {String(index + 1).padStart(2, "0")}
                  </div>
                  <div className="w-24 h-24 bg-gray-200 flex-shrink-0 overflow-hidden">
                    {doctor.photo ? (
                      <img
                        src={doctor.photo}
                        alt={doctor.name}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-gray-400 text-xs">
                        No Photo
                      </div>
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="text-xl font-bold mb-2">
                      {doctor.name || "Unnamed Dentist"}
                    </h3>
                    {doctor.email && (
                      <p className="text-sm text-gray-600 mb-1">
                        üìß {doctor.email}{" "}
                        {doctor.hasPassword && (
                          <span className="text-green-600 font-medium">
                            (Active)
                          </span>
                        )}
                        {!doctor.hasPassword && (
                          <span className="text-orange-600 font-medium">
                            (Pending setup)
                          </span>
                        )}
                      </p>
                    )}
                    <p className="text-gray-600 text-sm">
                      Join: {doctor.joinDate || "-"} | Days Done:{" "}
                      {doctor.daysDone || "-"}
                    </p>
                  </div>
                  <div className="text-right flex-shrink-0">
                    <div className="flex gap-3 mt-2 justify-end">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          setEditingIndex(index);
                        }}
                        className="text-gray-600 hover:text-gray-900 text-sm font-medium"
                      >
                        EDIT ‚Üí
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="border-t border-gray-200 pt-4">
        <button
          onClick={addToArray}
          className="px-4 py-2 bg-blue-500 text-white hover:bg-blue-600 text-sm font-semibold"
        >
          + Add Dentist
        </button>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";

interface FAQ {
  q: string;
  a: string;
}

export default function FAQsAdminPage() {
  const [data, setData] = useState<FAQ[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch("/api/clinic-info");
      if (!res.ok) throw new Error("Failed to fetch");
      const result = await res.json();
      setData(result.faqs || []);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // Fetch current data to merge
      const res = await fetch("/api/clinic-info");
      const currentData = await res.json();
      const updatedData = { ...currentData, faqs: data };

      const saveRes = await fetch("/api/clinic-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });
      if (!saveRes.ok) throw new Error("Failed to save");
      alert("Saved successfully!");
    } catch (err: any) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const updateArrayItem = (index: number, field: string, value: any) => {
    setData((prev) =>
      prev.map((item, i) => (i === index ? { ...item, [field]: value } : item))
    );
  };

  const addToArray = (item: FAQ) => {
    setData((prev) => [...prev, item]);
  };

  const removeFromArray = (index: number) => {
    setData((prev) => prev.filter((_, i) => i !== index));
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="max-w-4xl mx-auto space-y-8 px-4 sm:px-6">
      <div className="flex justify-between items-center">
        <div>
          <Link
            href="/admin/clinic"
            className="font-black hover:underline mb-1 inline-block text-m"
          >
            Back‚Üê
          </Link>
          <h1 className="text-2xl font-bold">FAQs Management</h1>
        </div>
        <button
          onClick={handleSave}
          disabled={saving}
          className="w-full md:w-auto px-6 py-2 border-2 border-black font-medium hover:bg-black hover:text-white transition-colors"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </div>

      {error && (
        <div className="text-red-600 bg-red-50 p-3 rounded">{error}</div>
      )}

      {/* FAQs Section */}
      <div className="bg-white border rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">
          Frequently Asked Questions
        </h2>
        {data.map((faq, index) => (
          <div key={index} className="mb-4 p-4 border rounded">
            <div className="mb-2">
              <label className="block text-sm font-medium mb-1">Question</label>
              <input
                type="text"
                value={faq.q}
                onChange={(e) => updateArrayItem(index, "q", e.target.value)}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Answer</label>
              <textarea
                value={faq.a}
                onChange={(e) => updateArrayItem(index, "a", e.target.value)}
                rows={2}
                className="w-full p-2 border rounded"
              />
            </div>
            <button
              onClick={() => removeFromArray(index)}
              className="mt-2 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
            >
              Delete
            </button>
          </div>
        ))}
        <button
          onClick={() => addToArray({ q: "", a: "" })}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Add FAQ
        </button>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";

interface GalleryItem {
  before: string;
  after: string;
  label: string;
}

export default function GalleryAdminPage() {
  const [data, setData] = useState<GalleryItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [uploading, setUploading] = useState<{
    index: number;
    field: string;
  } | null>(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch("/api/clinic-info");
      if (!res.ok) throw new Error("Failed to fetch");
      const result = await res.json();
      setData(result.gallery || []);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // Fetch current data to merge
      const res = await fetch("/api/clinic-info");
      const currentData = await res.json();
      const updatedData = { ...currentData, gallery: data };

      const saveRes = await fetch("/api/clinic-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });
      if (!saveRes.ok) throw new Error("Failed to save");
      alert("Saved successfully!");
    } catch (err: any) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const updateArrayItem = (index: number, field: string, value: any) => {
    setData((prev) =>
      prev.map((item, i) => (i === index ? { ...item, [field]: value } : item))
    );
  };

  const addToArray = (item: GalleryItem) => {
    setData((prev) => [...prev, item]);
  };

  const removeFromArray = (index: number) => {
    setData((prev) => prev.filter((_, i) => i !== index));
  };

  const handleFileUpload = async (file: File, index: number, field: string) => {
    setUploading({ index, field });
    setError(null);
    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("/api/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Upload failed");
      }

      const result = await response.json();
      updateArrayItem(index, field, result.imageUrl);
    } catch (err: any) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(null);
    }
  };

  const handleImageChange = async (
    e: React.ChangeEvent<HTMLInputElement>,
    index: number,
    field: string
  ) => {
    const file = e.target.files?.[0];
    if (file) {
      await handleFileUpload(file, index, field);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="max-w-4xl mx-auto space-y-8 px-4 sm:px-6">
      <div className="flex justify-between items-center">
        <div>
          <Link
            href="/admin/clinic"
            className="font-black hover:underline mb-1 inline-block text-m"
          >
            Back‚Üê
          </Link>
          <h1 className="text-2xl font-bold">Gallery Management</h1>
        </div>
        <button
          onClick={handleSave}
          disabled={saving}
          className="w-full md:w-auto px-6 py-2 border-2 border-black font-medium hover:bg-black hover:text-white transition-colors"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </div>

      {error && (
        <div className="text-red-600 bg-red-50 p-3 rounded">{error}</div>
      )}

      {/* Gallery Section */}
      <div className="bg-white border rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Gallery</h2>
        {data.map((item, index) => (
          <div key={index} className="mb-6 p-4 border rounded">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm font-medium mb-1">
                  Before Image
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleImageChange(e, index, "before")}
                  className="w-full p-2 border rounded mb-2"
                  disabled={
                    uploading?.index === index && uploading?.field === "before"
                  }
                />
                {uploading?.index === index &&
                  uploading?.field === "before" && (
                    <p className="text-sm text-blue-600 mb-2">Uploading...</p>
                  )}
                {item.before && (
                  <img
                    src={item.before}
                    alt="Before preview"
                    className="w-full h-32 object-cover rounded mb-2"
                  />
                )}
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">
                  After Image
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleImageChange(e, index, "after")}
                  className="w-full p-2 border rounded mb-2"
                  disabled={
                    uploading?.index === index && uploading?.field === "after"
                  }
                />
                {uploading?.index === index && uploading?.field === "after" && (
                  <p className="text-sm text-blue-600 mb-2">Uploading...</p>
                )}
                {item.after && (
                  <img
                    src={item.after}
                    alt="After preview"
                    className="w-full h-32 object-cover rounded mb-2"
                  />
                )}
              </div>
            </div>
            <div className="mb-3">
              <label className="block text-sm font-medium mb-1">Label</label>
              <input
                type="text"
                value={item.label}
                onChange={(e) =>
                  updateArrayItem(index, "label", e.target.value)
                }
                className="w-full p-2 border rounded"
                placeholder="Treatment name or description"
              />
            </div>
            <button
              onClick={() => removeFromArray(index)}
              className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
            >
              Delete Item
            </button>
          </div>
        ))}
        <button
          onClick={() => addToArray({ before: "", after: "", label: "" })}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Add Gallery Item
        </button>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";

interface HomepageData {
  heroTitle: string;
  heroSubtitle: string;
  heroDescription: string;
  heroImage: string;

  aboutTitle: string;
  aboutDescription: string;
}

const defaultHomepage: HomepageData = {
  heroTitle: "Senyum Percaya Diri di NOERDENTAL",
  heroSubtitle: "Hai! Senang banget kamu mampir ke NOERDENTAL CLINIC.",
  heroDescription:
    "Kami tahu, ke dokter gigi sering bikin deg-degan, tapi tenang‚Ä¶ di sini suasananya nyaman dan santai kok. Dokter dan tim kami siap membantu mulai dari perawatan ringan sampai estetik biar senyummu makin pede. Yuk, booking jadwal sekarang dan rasain sendiri perbedaannya!",
  heroImage: "/uploads/hero.jpg",
  aboutTitle:
    "NOERDENTAL Clinic adalah salah satu klinik dokter gigi terbaik di daerah pesisir selatan yang berkomitmen untuk terus berusaha memberikan pelayanan, kualitas kerja, dan fasilitas yang melebihi ekspektasi pasien.",
  aboutDescription:
    "NOERDENTAL Clinic adalah salah satu klinik dokter gigi terbaik di daerah pesisir selatan yang berkomitmen untuk terus berusaha memberikan pelayanan, kualitas kerja, dan fasilitas yang melebihi ekspektasi pasien.",
};

export default function HomepageAdminPage() {
  const [data, setData] = useState<HomepageData>(defaultHomepage);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await fetch("/api/clinic-info");
      if (!res.ok) throw new Error("Failed to fetch");
      const result = await res.json();
      setData({ ...defaultHomepage, ...result.homepage });
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!data) return;
    setSaving(true);
    try {
      // Fetch current data to merge
      const res = await fetch("/api/clinic-info");
      const currentData = await res.json();
      const updatedData = { ...currentData, homepage: data };

      const saveRes = await fetch("/api/clinic-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedData),
      });
      if (!saveRes.ok) throw new Error("Failed to save");
      alert("Saved successfully!");
    } catch (err: any) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const updateData = (field: string, value: any) => {
    setData((prev) => (prev ? { ...prev, [field]: value } : prev));
  };

  const handleFileUpload = async (file: File) => {
    setUploading(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("/api/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Upload failed");
      }

      const result = await response.json();
      updateData("heroImage", result.imageUrl);
    } catch (err: any) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  const handleImageChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      await handleFileUpload(file);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!data) return <div>Error loading data</div>;

  return (
    <div className="max-w-4xl mx-auto space-y-8 px-4 sm:px-6">
      <div className="flex justify-between items-center">
        <div>
          <Link
            href="/admin/clinic"
            className="font-black hover:underline mb-1 inline-block text-m"
          >
            Back‚Üê
          </Link>
          <h1 className="text-2xl font-bold">Homepage Settings</h1>
        </div>
        <button
          onClick={handleSave}
          disabled={saving}
          className="w-full md:w-auto px-6 py-2 border-2 border-black font-medium hover:bg-black hover:text-white transition-colors"
        >
          {saving ? "Saving..." : "Save Changes"}
        </button>
      </div>

      {error && (
        <div className="text-red-600 bg-red-50 p-3 rounded">{error}</div>
      )}

      {/* Homepage Section */}
      <div className="bg-white border rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Homepage Configuration</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-1">Hero Title</label>
            <input
              type="text"
              value={data.heroTitle}
              onChange={(e) => updateData("heroTitle", e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              Hero Subtitle
            </label>
            <input
              type="text"
              value={data.heroSubtitle}
              onChange={(e) => updateData("heroSubtitle", e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Hero Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="w-full p-2 border rounded mb-2"
              disabled={uploading}
            />
            {uploading && (
              <p className="text-sm text-blue-600 mb-2">Uploading...</p>
            )}
            {data.heroImage && (
              <div className="mb-2">
                <img
                  src={data.heroImage}
                  alt="Hero preview"
                  className="w-32 h-32 object-cover rounded"
                />
              </div>
            )}
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              Hero Description
            </label>
            <textarea
              value={data.heroDescription}
              onChange={(e) => updateData("heroDescription", e.target.value)}
              rows={4}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              About Title
            </label>
            <input
              type="text"
              value={data.aboutTitle}
              onChange={(e) => updateData("aboutTitle", e.target.value)}
              className="w-full p-2 border rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">
              About Description
            </label>
            <textarea
              value={data.aboutDescription}
              onChange={(e) => updateData("aboutDescription", e.target.value)}
              rows={4}
              className="w-full p-2 border rounded"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useEffect, useMemo, useState } from "react";

// Types for Service and Visit creation
type Service = {
  id: string;
  name: string;
  price: any;
};

type VisitServicePayload = {
  serviceId: string;
  quantity: number;
  customName?: string;
  customPrice?: number;
};

type VisitPayload = {
  id?: string;
  patientName: string;
  services: VisitServicePayload[];
  discount: number;
  paymentMethod?: string;
  status?: "paid" | "unpaid";
  notes?: string;
  date?: string;
};

export default function ReportsPage() {
  const [services, setServices] = useState<Service[]>([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [rows, setRows] = useState<any[]>([]);
  const [isAddingNew, setIsAddingNew] = useState(false);
  const [editingVisit, setEditingVisit] = useState<VisitPayload | null>(null);
  const [searchName, setSearchName] = useState("");
  const [searchDate, setSearchDate] = useState("");
  const [searchService, setSearchService] = useState("");

  // Simple form state
  const [form, setForm] = useState<VisitPayload>({
    patientName: "",
    services: [{ serviceId: "", quantity: 1 }],
    discount: 0,
    paymentMethod: "cash",
    status: "paid",
    notes: "",
    date: "",
  });

  const [showDiscount, setShowDiscount] = useState(false);

  const calculateTotal = useMemo(() => {
    let total = 0;
    for (const svc of form.services) {
      if (svc.serviceId === "custom") {
        // Custom service
        total += (svc.customPrice || 0) * svc.quantity;
      } else if (svc.serviceId) {
        // Regular service
        const service = services.find((s) => s.id === svc.serviceId);
        if (service) {
          total += Number(service.price) * svc.quantity;
        }
      }
    }
    return Math.max(0, total - form.discount);
  }, [services, form.services, form.discount]);

  useEffect(() => {
    setLoading(true);
    fetch("/api/services?isActive=true")
      .then((r) => r.json())
      .then((data) => setServices(data))
      .finally(() => setLoading(false));
  }, []);

  const fetchVisits = async () => {
    const params = new URLSearchParams();
    if (searchName) params.append("patientName", searchName);
    if (searchDate) params.append("date", searchDate);
    if (searchService) params.append("serviceName", searchService);
    const res = await fetch(`/api/visits?${params}`);
    if (res.ok) {
      const data = await res.json();
      setRows(data);
    }
  };

  useEffect(() => {
    fetchVisits();
  }, [searchName, searchDate, searchService]);

  const onChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement
    >
  ) => {
    const { name, value } = e.target;
    if (name === "discount") {
      const val = value.replace(/^Rp\s*/, "").replace(/[^\d]/g, "");
      const num = val ? Number(val) : 0;
      setForm((f) => ({ ...f, discount: num }));
    } else {
      setForm((f) => ({ ...f, [name]: value }));
    }
  };

  const addService = () => {
    setForm((f) => ({
      ...f,
      services: [...f.services, { serviceId: "", quantity: 1 }],
    }));
  };

  const removeService = (index: number) => {
    setForm((f) => ({
      ...f,
      services: f.services.filter((_, i) => i !== index),
    }));
  };

  const updateService = (
    index: number,
    updates: Partial<VisitServicePayload>
  ) => {
    setForm((f) => ({
      ...f,
      services: f.services.map((s, i) =>
        i === index ? { ...s, ...updates } : s
      ),
    }));
  };

  const resetForm = () => {
    setForm({
      patientName: "",
      services: [{ serviceId: "", quantity: 1 }],
      discount: 0,
      paymentMethod: "cash",
      status: "paid",
      notes: "",
      date: "",
    });
    setShowDiscount(false);
  };

  const cancelEdit = () => {
    setIsAddingNew(false);
    setEditingVisit(null);
    resetForm();
  };

  const onSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const method = editingVisit ? "PUT" : "POST";
      const body = editingVisit ? { id: editingVisit.id, ...form } : form;
      const res = await fetch("/api/visits", {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      if (!res.ok) {
        const j = await res.json().catch(() => ({}));
        throw new Error(j?.error || "Failed to submit");
      }
      const data = await res.json();
      if (editingVisit) {
        setRows((r) =>
          r.map((row) => (row.id === editingVisit.id ? data : row))
        );
        setEditingVisit(null);
      } else {
        setRows((r) => [data, ...r]);
        setIsAddingNew(false);
      }
      resetForm();
      fetchVisits();
    } catch (err) {
      console.error(err);
      alert((err as Error).message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (visit: any) => {
    setEditingVisit(visit);
    setForm({
      id: visit.id,
      patientName: visit.patientName,
      services: visit.services?.map((vs: any) => ({
        serviceId: vs.serviceId,
        quantity: vs.quantity,
        customName: vs.customName,
        customPrice: vs.customPrice,
      })) || [{ serviceId: "", quantity: 1 }],
      discount: Number(visit.discount) || 0,
      paymentMethod: visit.paymentMethod,
      status: visit.status,
      notes: visit.notes || "",
      date: visit.date ? new Date(visit.date).toISOString().split("T")[0] : "",
    });
    setShowDiscount(Number(visit.discount) > 0);
    setIsAddingNew(true);
    setTimeout(() => {
      const element = document.getElementById("edit-form");
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }, 100);
  };

  const handleDelete = async (id: string) => {
    if (!confirm("Are you sure you want to delete this visit?")) return;
    try {
      const res = await fetch(`/api/visits?id=${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete");
      setRows((r) => r.filter((row) => row.id !== id));
    } catch (err) {
      console.error(err);
      alert((err as Error).message);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-3">
        <div>
          <h1 className="text-2xl font-bold">Patient Reports</h1>
          <p className="text-gray-600">Manage patient visit reports</p>
        </div>
        <button
          onClick={() => {
            setIsAddingNew(true);
            setTimeout(() => {
              const element = document.getElementById("edit-form");
              if (element) {
                element.scrollIntoView({ behavior: "smooth", block: "start" });
              }
            }, 100);
          }}
          className="w-full md:w-auto px-6 py-2 bg-black text-white font-medium hover:bg-gray-800 transition-colors"
        >
          + Add Patient
        </button>
      </div>

      {/* Input Form */}
      {(isAddingNew || editingVisit) && (
        <div
          id="edit-form"
          className="bg-gray-50 border border-gray-200 p-4 sm:p-6"
        >
          <h2 className="text-xl font-bold mb-4">
            {editingVisit ? "Edit Patient Report" : "Add New Patient Report"}
          </h2>
          <form onSubmit={onSubmit} className="space-y-6">
            {/* Basic Info Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium mb-2">
                  Patient Name *
                </label>
                <input
                  name="patientName"
                  value={form.patientName}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">Date</label>
                <input
                  name="date"
                  type="date"
                  value={form.date}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                />
              </div>
            </div>

            {/* Services */}
            <div>
              <div className="flex justify-between items-center mb-2">
                <label className="block text-sm font-medium">Services *</label>
                <button
                  type="button"
                  onClick={addService}
                  className="text-sm text-blue-600 hover:text-blue-800"
                >
                  + Add Service
                </button>
              </div>
              {form.services.map((svc, index) => (
                <div
                  key={index}
                  className="mb-4 p-3 border border-gray-200 rounded bg-white"
                >
                  <div className="flex gap-2 mb-2">
                    <select
                      value={svc.serviceId}
                      onChange={(e) =>
                        updateService(index, {
                          serviceId: e.target.value,
                          customName: undefined,
                          customPrice: undefined,
                        })
                      }
                      className="flex-1 p-2 border border-gray-300 focus:outline-none focus:border-black"
                      required
                    >
                      <option value="">Select service</option>
                      {services.map((s) => (
                        <option key={s.id} value={s.id}>
                          {s.name} - Rp{" "}
                          {Number(s.price).toLocaleString("id-ID")}
                        </option>
                      ))}
                      <option value="custom">
                        ‚ûï Custom Service (Type Manually)
                      </option>
                    </select>
                    <input
                      type="number"
                      min="1"
                      value={svc.quantity}
                      onChange={(e) =>
                        updateService(index, {
                          quantity: Number(e.target.value),
                        })
                      }
                      className="w-20 p-2 border border-gray-300 focus:outline-none focus:border-black"
                    />
                    {form.services.length > 1 && (
                      <button
                        type="button"
                        onClick={() => removeService(index)}
                        className="px-3 py-2 text-red-600 hover:text-red-800 font-bold text-xl"
                      >
                        √ó
                      </button>
                    )}
                  </div>

                  {/* Custom Service Fields */}
                  {svc.serviceId === "custom" && (
                    <div className="grid grid-cols-2 gap-2 mt-2 pl-1">
                      <div>
                        <input
                          type="text"
                          placeholder="Service name"
                          value={svc.customName || ""}
                          onChange={(e) =>
                            updateService(index, { customName: e.target.value })
                          }
                          className="w-full p-2 border border-gray-300 focus:outline-none focus:border-black text-sm"
                          required
                        />
                      </div>
                      <div>
                        <input
                          type="number"
                          placeholder="Price"
                          value={svc.customPrice || ""}
                          onChange={(e) =>
                            updateService(index, {
                              customPrice: Number(e.target.value),
                            })
                          }
                          className="w-full p-2 border border-gray-300 focus:outline-none focus:border-black text-sm"
                          required
                        />
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Price & Discount Section */}
            <div className="space-y-3">
              {!showDiscount && (
                <button
                  type="button"
                  onClick={() => setShowDiscount(true)}
                  className="text-sm text-blue-600 hover:text-blue-800"
                >
                  + Add Discount
                </button>
              )}

              {showDiscount && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <div className="flex justify-between items-center mb-2">
                      <label className="block text-sm font-medium">
                        Discount
                      </label>
                      <button
                        type="button"
                        onClick={() => {
                          setShowDiscount(false);
                          setForm((f) => ({ ...f, discount: 0 }));
                        }}
                        className="text-xs text-red-600 hover:text-red-800"
                      >
                        Remove
                      </button>
                    </div>
                    <input
                      name="discount"
                      type="text"
                      value={`Rp ${form.discount.toLocaleString("id-ID")}`}
                      onChange={onChange}
                      className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                    />
                  </div>
                </div>
              )}

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Total
                  </label>
                  <input
                    type="text"
                    value={`Rp ${calculateTotal.toLocaleString("id-ID")}`}
                    readOnly
                    className="w-full p-3 border border-gray-300 bg-gray-100 font-bold"
                  />
                </div>
              </div>
            </div>

            {/* Payment Info */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium mb-2">Status</label>
                <select
                  name="status"
                  value={form.status}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                >
                  <option value="paid">Paid</option>
                  <option value="unpaid">Unpaid</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Payment Method
                </label>
                <select
                  name="paymentMethod"
                  value={form.paymentMethod}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                >
                  <option value="cash">Cash</option>
                  <option value="transfer_bank">Transfer Bank</option>
                  <option value="e-wallet">E-Wallet</option>
                </select>
              </div>
            </div>

            {/* Descriptions */}
            <div>
              <label className="block text-sm font-medium mb-2">Notes</label>
              <textarea
                name="notes"
                value={form.notes}
                onChange={onChange}
                rows={3}
                className="w-full p-3 border border-gray-300 focus:outline-none focus:border-black"
                placeholder="Additional notes"
              />
            </div>

            {/* Submit Buttons */}
            <div className="flex flex-col sm:flex-row gap-3 sm:gap-4">
              <button
                type="submit"
                disabled={submitting}
                className="w-full sm:w-auto px-6 py-2 bg-black text-white font-medium hover:bg-gray-800 transition-colors disabled:opacity-50"
              >
                {editingVisit ? "Update Report" : "Save Report"}
              </button>
              <button
                type="button"
                onClick={cancelEdit}
                className="w-full sm:w-auto px-6 py-2 border border-gray-300 text-gray-700 font-medium hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Search */}
      <div className="bg-white p-4 rounded-md border">
        <div className="flex flex-col md:flex-row gap-4">
          <input
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            className="w-full border rounded-md px-3 py-2"
            placeholder="Patient Name"
          />
          <input
            type="date"
            value={searchDate}
            onChange={(e) => setSearchDate(e.target.value)}
            className="w-full border rounded-md px-3 py-2"
          />
          <input
            value={searchService}
            onChange={(e) => setSearchService(e.target.value)}
            className="w-full border rounded-md px-3 py-2"
            placeholder="Service"
          />
        </div>
      </div>

      {/* Table */}
      <div className="bg-white border rounded-md">
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm">
            <thead className="bg-gray-100 text-gray-700">
              <tr>
                <th className="text-left px-3 py-2">Date</th>
                <th className="text-left px-3 py-2">Patient</th>
                <th className="text-left px-3 py-2">Service</th>
                <th className="text-right px-3 py-2">Price</th>
                <th className="text-left px-3 py-2">Payment</th>
                <th className="text-left px-3 py-2">Status</th>
                <th className="text-left px-3 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {rows.length === 0 ? (
                <tr>
                  <td
                    colSpan={7}
                    className="px-3 py-6 text-center text-gray-500"
                  >
                    No reports found.
                  </td>
                </tr>
              ) : (
                rows.map((r, i) => (
                  <tr key={r.id || i} className="border-t">
                    <td className="px-3 py-2">
                      {r.date ? new Date(r.date).toLocaleDateString() : "-"}
                    </td>
                    <td className="px-3 py-2">{r.patientName}</td>
                    <td className="px-3 py-2">
                      {r.services
                        ?.map((vs: any) => vs.customName || vs.service?.name)
                        .join(", ") || "N/A"}
                    </td>
                    <td className="px-3 py-2 text-right">
                      Rp {Number(r.total).toLocaleString("id-ID")}
                    </td>
                    <td className="px-3 py-2">{r.paymentMethod}</td>
                    <td className="px-3 py-2">{r.status}</td>
                    <td className="px-3 py-2">
                      <button
                        onClick={() => handleEdit(r)}
                        className="text-gray-600 hover:text-gray-800 mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(r.id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";
import { z } from "zod";
import bcrypt from "bcryptjs";
import { createSession } from "@/lib/auth";

const LoginSchema = z.object({
  email: z.string().email("Email tidak valid"),
  password: z.string().min(6, "Password minimal 6 karakter"),
});

export async function POST(req: NextRequest) {
  try {
    console.log("=== Login Request Started ===");

    const body = await req.json();
    console.log("Body received:", { email: body.email });

    const parsed = LoginSchema.safeParse(body);

    if (!parsed.success) {
      console.log("Validation failed:", parsed.error.errors);
      const errors = parsed.error.errors
        .map((err) => `${err.path.join(".")}: ${err.message}`)
        .join(", ");

      return NextResponse.json({ error: errors }, { status: 400 });
    }

    const { email, password } = parsed.data;
    console.log("Looking for user:", email);

    const user = await prisma.user.findUnique({ where: { email } });
    console.log("User found:", user ? "Yes" : "No");

    if (!user) {
      return NextResponse.json(
        { error: "Email atau password salah" },
        { status: 401 }
      );
    }

    // Cek apakah user aktif
    if (!user.isActive) {
      return NextResponse.json(
        { error: "Akun tidak aktif. Hubungi administrator." },
        { status: 403 }
      );
    }

    // Cek apakah password sudah diset
    if (!user.passwordHash) {
      return NextResponse.json(
        { error: "Password belum diset. Silakan gunakan kode OTP Anda." },
        { status: 400 }
      );
    }

    console.log("Comparing password...");
    const ok = await bcrypt.compare(password, user.passwordHash);
    console.log("Password match:", ok);

    if (!ok) {
      return NextResponse.json(
        { error: "Email atau password salah" },
        { status: 401 }
      );
    }

    console.log("Creating session for user:", user.id);
    await createSession({
      sub: user.id,
      email: user.email,
      name: user.name,
      role: user.role as any,
    });

    console.log("Session created successfully");
    return NextResponse.json({ ok: true });
  } catch (error) {
    console.error("=== Login Error ===");
    console.error(
      "Error type:",
      error instanceof Error ? error.constructor.name : typeof error
    );
    console.error(
      "Error message:",
      error instanceof Error ? error.message : error
    );
    console.error(
      "Error stack:",
      error instanceof Error ? error.stack : "No stack trace"
    );

    return NextResponse.json(
      { error: "Terjadi kesalahan server" },
      { status: 500 }
    );
  }
}

// app/api/auth/me/route.ts
import { NextResponse } from "next/server";
import { getSession } from "@/lib/auth";

export async function GET() {
  try {
    const session = await getSession();

    if (!session) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    return NextResponse.json({
      id: session.sub,
      email: session.email,
      name: session.name,
      role: session.role,
    });
  } catch (error: any) {
    return NextResponse.json(
      { error: error.message || "Failed to get user info" },
      { status: 500 }
    );
  }
}

// app/api/auth/setup-password/route.ts
import { NextRequest, NextResponse } from "next/server";
import prisma from "@/lib/prisma";
import bcrypt from "bcryptjs";
import { createSession } from "@/lib/auth";

export async function POST(req: NextRequest) {
  try {
    const body = await req.json();
    const { email, otp, password } = body;

    if (!email || !otp || !password) {
      return NextResponse.json(
        { error: "Email, OTP, and password are required" },
        { status: 400 }
      );
    }

    // Validasi password minimal 6 karakter
    if (password.length < 6) {
      return NextResponse.json(
        { error: "Password must be at least 6 characters" },
        { status: 400 }
      );
    }

    // Cari user dengan email dan OTP
    const user = await prisma.user.findFirst({
      where: {
        email,
        otp,
        role: "DOCTOR",
      },
    });

    if (!user) {
      return NextResponse.json(
        { error: "Invalid email or OTP" },
        { status: 400 }
      );
    }

    // Cek apakah OTP masih berlaku
    if (user.otpExpiry && user.otpExpiry < new Date()) {
      return NextResponse.json(
        { error: "OTP has expired. Please request a new one." },
        { status: 400 }
      );
    }

    // Cek apakah user aktif
    if (!user.isActive) {
      return NextResponse.json(
        { error: "Account is inactive. Please contact administrator." },
        { status: 403 }
      );
    }

    // Hash password
    const passwordHash = await bcrypt.hash(password, 10);

    // Update user: set password, hapus OTP
    await prisma.user.update({
      where: { id: user.id },
      data: {
        passwordHash,
        otp: null,
        otpExpiry: null,
      },
    });

    // Create session
    await createSession({
      sub: user.id,
      email: user.email,
      name: user.name,
      role: "DOCTOR",
    });

    return NextResponse.json({
      message: "Password set successfully. You are now logged in.",
      user: {
        id: user.id,
        name: user.name,
        email: user.email,
        role: "DOCTOR",
      },
    });
