// POST: Daftarkan dentist baru
export async function POST(req: NextRequest) {
  try {
    const session = await requireAdmin();
    const body = await req.json();

    const { name, email } = body;

    if (!name || !email) {
      return NextResponse.json(
        { error: "Name and email are required" },
        { status: 400 }
      );
    }

    // Cek apakah email sudah digunakan
    const existing = await prisma.user.findUnique({
      where: { email },
    });

    if (existing) {
      return NextResponse.json(
        { error: "Email already registered" },
        { status: 400 }
      );
    }

    // Generate OTP
    const otp = generateOTP();
    const otpExpiry = new Date();
    otpExpiry.setHours(otpExpiry.getHours() + 24); // OTP berlaku 24 jam

    // Buat dentist baru
    const dentist = await prisma.user.create({
      data: {
        name,
        email,
        role: "DOCTOR",
        otp,
        otpExpiry,
        isActive: true,
        createdById: session.sub,
      },
      select: {
        id: true,
        name: true,
        email: true,
        isActive: true,
        createdAt: true,
      },
    });

    const emailSent = await sendOTPEmail(email, name, otp);
    if (!emailSent) {
      await prisma.user.delete({ where: { id: dentist.id } });
      return NextResponse.json(
        { error: "Failed to send OTP email" },
        { status: 500 }
      );
    }

    return NextResponse.json(
      {
        message: "Dentist registered successfully. OTP sent to email.",
        dentist,
      },
      { status: 201 }
    );
  } catch (error: any) {
    return NextResponse.json(
      { error: error.message || "Failed to register dentist" },
      {
        status:
          error.message === "Unauthorized"
            ? 401
            : error.message === "Forbidden: Insufficient permissions"
            ? 403
            : 500,
      }
    );
  }
}

