// POST: Create or Update doctor profile
export async function POST(req: NextRequest) {
  try {
    await requireAdmin();
    const body = await req.json();

    const { id, name, email, photo, joinDate, daysDone, order } = body;

    // Jika update existing doctor
    if (id) {
      const existingDoctor = await prisma.doctorProfile.findUnique({
        where: { id: parseInt(id) },
        include: { user: true },
      });

      if (!existingDoctor) {
        return NextResponse.json(
          { error: "Doctor not found" },
          { status: 404 }
        );
      }

      // Update doctor profile
      const updatedDoctor = await prisma.doctorProfile.update({
        where: { id: parseInt(id) },
        data: {
          name,
          email: email || null,
          photo,
          joinDate,
          daysDone,
          order: order || 0,
        },
      });

      // Jika email berubah atau baru ditambahkan
      if (email && email !== existingDoctor.email) {
        // Cek apakah email sudah digunakan
        const existingUser = await prisma.user.findUnique({
          where: { email },
        });

        if (existingUser && existingUser.id !== existingDoctor.userId) {
          return NextResponse.json(
            { error: "Email already registered" },
            { status: 400 }
          );
        }

        // Jika belum ada user, buat user baru
        if (!existingDoctor.userId) {
          const otp = generateOTP();
          const otpExpiry = new Date();
          otpExpiry.setHours(otpExpiry.getHours() + 24);

          const user = await prisma.user.create({
            data: {
              name,
              email,
              role: "DOCTOR",
              otp,
              otpExpiry,
              isActive: true,
            },
          });

          // Link user ke doctor profile
          await prisma.doctorProfile.update({
            where: { id: parseInt(id) },
            data: {
              userId: user.id,
            },
          });

          // Kirim OTP via email
          await sendOTPEmail(email, name, otp);
        } else {
          // Update email user yang sudah ada
          await prisma.user.update({
            where: { id: existingDoctor.userId },
            data: {
              email,
              name,
            },
          });
        }
      }

      return NextResponse.json(updatedDoctor);
    }

    // Create new doctor
    const newDoctor = await prisma.doctorProfile.create({
      data: {
        name,
        email: email || null,
        photo,
        joinDate,
        daysDone,
        order: order || 0,
      },
    });

    // Jika ada email, buat user account
    if (email) {
      const otp = generateOTP();
      const otpExpiry = new Date();
      otpExpiry.setHours(otpExpiry.getHours() + 24);

      const user = await prisma.user.create({
        data: {
          name,
          email,
          role: "DOCTOR",
          otp,
          otpExpiry,
          isActive: true,
        },
      });

      // Link user ke doctor profile
      await prisma.doctorProfile.update({
        where: { id: newDoctor.id },
        data: {
          userId: user.id,
        },
      });

      // Kirim OTP via email
      await sendOTPEmail(email, name, otp);
    }

    return NextResponse.json(newDoctor, { status: 201 });
  } catch (error: any) {
    console.error("Error creating/updating doctor:", error);
    return NextResponse.json(
      { error: error.message || "Failed to create/update doctor" },
      {
        status:
          error.message === "Unauthorized"
            ? 401
            : error.message === "Forbidden: Insufficient permissions"
            ? 403
            : 500,
      }
    );
  }
}

// DELETE: Delete doctor profile
export async function DELETE(req: NextRequest) {
  try {
    await requireAdmin();
    const { searchParams } = new URL(req.url);
    const id = searchParams.get("id");

    if (!id) {
      return NextResponse.json(
        { error: "Doctor ID required" },
        { status: 400 }
      );
    }

    const doctor = await prisma.doctorProfile.findUnique({
      where: { id: parseInt(id) },
    });

    if (!doctor) {
      return NextResponse.json({ error: "Doctor not found" }, { status: 404 });
    }

    // Delete user if exists
    if (doctor.userId) {
      await prisma.user.delete({
        where: { id: doctor.userId },
      });
    }

    // Delete doctor profile
    await prisma.doctorProfile.delete({
      where: { id: parseInt(id) },
    });

    return NextResponse.json({ message: "Doctor deleted successfully" });
  } catch (error: any) {
    console.error("Error deleting doctor:", error);
    return NextResponse.json(
      { error: error.message || "Failed to delete doctor" },
      {
        status:
          error.message === "Unauthorized"
            ? 401
            : error.message === "Forbidden: Insufficient permissions"
            ? 403
            : 500,
      }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";
import ExcelJS from "exceljs";
import { z } from "zod";

const QuerySchema = z.object({
  from: z.string().optional(),
  to: z.string().optional(),
  category: z.string().optional(),
  mode: z.enum(["single", "per-category"]).default("single"),
});

import { getSession } from "@/lib/auth";

export async function GET(req: NextRequest) {
  const session = await getSession();
  if (!session)
    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });

  const { searchParams } = new URL(req.url);
  const parse = QuerySchema.safeParse(Object.fromEntries(searchParams));
  if (!parse.success) {
    return NextResponse.json({ error: parse.error.flatten() }, { status: 400 });
  }
  const { from, to, category, mode } = parse.data;

  const where: any = {};
  if (from || to) {
    where.date = {
      gte: from ? new Date(from) : undefined,
      lte: to ? new Date(to) : undefined,
    };
  }
  if (category) where.category = category;

  const visits = await prisma.visit.findMany({
    where,
    include: { service: true },
    orderBy: { date: "asc" },
  });

  const workbook = new ExcelJS.Workbook();
  const addSheet = (name: string, rows: typeof visits) => {
    const sheet = workbook.addWorksheet(name);
    sheet.columns = [
      { header: "Tanggal", key: "date", width: 16 },
      { header: "Nama Pasien", key: "patientName", width: 24 },
      { header: "Kategori", key: "category", width: 16 },
      { header: "Layanan", key: "service", width: 24 },
      { header: "Harga", key: "price", width: 12 },
      { header: "Diskon", key: "discount", width: 12 },
      { header: "Total", key: "total", width: 12 },
      { header: "Status", key: "status", width: 12 },
      { header: "Pembayaran", key: "paymentMethod", width: 14 },
      { header: "Catatan", key: "notes", width: 24 },
    ];
    rows.forEach((v) => {
      sheet.addRow({
        date: v.date.toISOString().slice(0, 10),
        patientName: v.patientName,
        category: v.category ?? "-",
        service: v.service.name,
        price: Number(v.price),
        discount: Number(v.discount),
        total: Number(v.total),
        status: v.status,
        paymentMethod: v.paymentMethod ?? "-",
        notes: v.notes ?? "",
      });
    });
  };

  if (mode === "per-category") {
    const byCat = new Map<string, typeof visits>();
    visits.forEach((v) => {
      if (!byCat.has(v.category ?? "Tanpa Kategori"))
        byCat.set(v.category ?? "Tanpa Kategori", []);
      byCat.get(v.category ?? "Tanpa Kategori")!.push(v);
    });
    if (byCat.size === 0) addSheet("Empty", []);
    else for (const [cat, rows] of byCat.entries()) addSheet(cat, rows);
  } else {
    addSheet("Visits", visits);
  }

  const buffer = await workbook.xlsx.writeBuffer();
  return new NextResponse(buffer, {
    status: 200,
    headers: {
      "Content-Type":
        "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
      "Content-Disposition": `attachment; filename="visits_export.xlsx"`,
    },
  });
}

import { NextResponse } from "next/server";
import { prisma } from "@/lib/prisma";

export async function GET() {
  const startedAt = Date.now();
  const result: {
    status: "ok";
    env: string | undefined;
    uptimeSec: number;
    db: { status: "up" | "down"; latencyMs: number | null; error?: string };
  } = {
    status: "ok",
    env: process.env.NODE_ENV,
    uptimeSec: Math.round(process.uptime()),
    db: { status: "down", latencyMs: null },
  };

  try {
    const t0 = Date.now();
    // Lightweight DB check
    await prisma.$queryRaw`SELECT 1`;
    const t1 = Date.now();
    result.db.status = "up";
    result.db.latencyMs = t1 - t0;
  } catch (err: any) {
    result.db.status = "down";
    if (process.env.NODE_ENV !== "production") {
      result.db.error = err?.message ?? String(err);
    }
  }

  // Overall request processing time (optional)
  const totalMs = Date.now() - startedAt;
  return NextResponse.json({ ...result, totalMs });
}

import { NextRequest, NextResponse } from "next/server";
import { readFile, stat } from "fs/promises";
import path from "path";

// Allowlist of image extensions we serve
const ALLOWED_EXTS = new Set([
  ".png",
  ".jpg",
  ".jpeg",
  ".webp",
  ".gif",
  ".avif",
]);

export async function GET(
  request: NextRequest,
  { params }: { params: { filename: string } }
) {
  const raw = params?.filename ?? "";

  try {
    // Basic validation
    if (!raw || typeof raw !== "string") {
      return NextResponse.json(
        { error: "filename is required" },
        { status: 400 }
      );
    }

    // Normalize to prevent path traversal, e.g. ../../secret
    const safeName = path.basename(raw);
    const ext = path.extname(safeName).toLowerCase();

    // Validate extension
    if (!ALLOWED_EXTS.has(ext)) {
      return NextResponse.json(
        { error: "unsupported file type" },
        { status: 400 }
      );
    }

    const uploadsDir = path.resolve(process.cwd(), "public", "uploads");
    const filePath = path.resolve(uploadsDir, safeName);

    // Ensure the resolved filePath is still inside uploadsDir
    if (!filePath.startsWith(uploadsDir + path.sep)) {
      return NextResponse.json({ error: "invalid path" }, { status: 400 });
    }

    // Read file and stat for caching headers
    const fileBuffer = await readFile(filePath);
    const { mtime } = await stat(filePath);

    // Determine content type
    const contentType =
      ext === ".png"
        ? "image/png"
        : ext === ".webp"
        ? "image/webp"
        : ext === ".gif"
        ? "image/gif"
        : ext === ".avif"
        ? "image/avif"
        : "image/jpeg"; // default for .jpg/.jpeg

    // Use Blob to satisfy BodyInit typing in NextResponse
    const body = new Blob([fileBuffer], { type: contentType });
    return new NextResponse(body, {
      headers: {
        "Content-Type": contentType,
        "Content-Disposition": `inline; filename="${safeName}"`,
        "Cache-Control": "public, max-age=86400, stale-while-revalidate=604800",
        "Last-Modified": mtime.toUTCString(),
      },
    });
  } catch (error: any) {
    if (error?.code === "ENOENT") {
      return new NextResponse("Image not found", { status: 404 });
    }
    console.error("Error serving image:", error);
    return NextResponse.json(
      { error: "Internal Server Error" },
      { status: 500 }
    );
  }
}

